import React, { useEffect, useState } from "react";
import { getAttendees, removeAttendee, updateAttendee } from "../api";

const AttendeeList = ({ eventId }) => {
  const [attendees, setAttendees] = useState([]);
  const [editingAttendee, setEditingAttendee] = useState(null);
  const [updatedData, setUpdatedData] = useState({ name: "", email: "" });

  // Fetch attendees when the component is mounted or eventId changes
  useEffect(() => {
    getAttendees(eventId).then((res) => setAttendees(res.data));
  }, [eventId]);

  // Remove attendee from the list
  const handleRemove = (id) => {
    removeAttendee(id).then(() => setAttendees(attendees.filter((a) => a.id !== id)));
  };

  // Update RSVP status of attendee
  const handleRSVP = (id) => {
    updateAttendee(eventId, id, { rsvp: true }).then((res) => {
      setAttendees(attendees.map((a) => (a.id === id ? res.data : a)));
    });
  };

  // Handle the editing form for attendee
  const handleEditClick = (attendee) => {
    setEditingAttendee(attendee);
    setUpdatedData({ name: attendee.name, email: attendee.email });
  };

  // Handle the update submit action
  const handleUpdate = (id) => {
    updateAttendee(eventId, id, updatedData).then((res) => {
      setAttendees(attendees.map((a) => (a.id === id ? res.data : a)));
      setEditingAttendee(null); // Close the edit form after update
    });
  };

  // Handle form data changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Attendees</h2>
      <ul>
        {attendees.map((attendee) => (
          <li key={attendee.id}>
            {attendee.name} - {attendee.email}

            {/* Remove button */}
            <button onClick={() => handleRemove(attendee.id)}>Remove</button>

            {/* RSVP button */}
            {!attendee.rsvp && <button onClick={() => handleRSVP(attendee.id)}>RSVP</button>}

            {/* Update button */}
            <button onClick={() => handleEditClick(attendee)}>Update</button>

            {/* Edit Form (conditionally rendered) */}
            {editingAttendee && editingAttendee.id === attendee.id && (
              <div>
                <input
                  type="text"
                  name="name"
                  value={updatedData.name}
                  onChange={handleInputChange}
                  placeholder="Name"
                />
                <input
                  type="email"
                  name="email"
                  value={updatedData.email}
                  onChange={handleInputChange}
                  placeholder="Email"
                />
                <button onClick={() => handleUpdate(attendee.id)}>Save</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AttendeeList;
