import React, { useEffect, useState } from "react";
import { getAttendees, removeAttendee, updateAttendee } from "../api";

const AttendeeList = ({ eventId }) => {
  const [attendees, setAttendees] = useState([]);

  useEffect(() => {
    getAttendees(eventId).then((res) => setAttendees(res.data));
  }, [eventId]);

  const handleRemove = (id) => {
    removeAttendee(id).then(() => setAttendees(attendees.filter((a) => a.id !== id)));
  };

  const handleRSVP = (id) => {
    updateAttendee(id, { rsvp: true }).then((res) => {
      setAttendees(attendees.map((a) => (a.id === id ? res.data : a)));
    });
  };

  return (
    <div>
      <h2>Attendees</h2>
      <ul>
        {attendees.map((attendee) => (
          <li key={attendee.id}>
            {attendee.name} - {attendee.email}
            <button onClick={() => handleRemove(attendee.id)}>Remove</button>
            {!attendee.rsvp && <button onClick={() => handleRSVP(attendee.id)}>RSVP</button>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AttendeeList;
